import React, { useState, useEffect } from 'react';
import ProductService from '../services/productservices';
import './dash.css';

function Dashboard() {
  const [products, setProducts] = useState([]);
  const [productData, setProductData] = useState({
    name: '',
    price: '',
    description: '',
    quantity: '',
    category_id: '',
    image: ''
  });
  const [message, setMessage] = useState('');
  const [isAdmin, setIsAdmin] = useState(false); // State to track admin status
  const [showCreateForm, setShowCreateForm] = useState(false); // State to toggle create form visibility
  const [showUpdateForm, setShowUpdateForm] = useState(false); // State to toggle update form visibility
  const [showDeleteForm, setShowDeleteForm] = useState(false); // State to toggle delete form visibility

  useEffect(() => {
    // Check if user is authenticated and admin
    const token = localStorage.getItem('accessToken'); // Example: Retrieve token from localStorage
    if (token) {
      checkAdminStatus(token); // Pass token to check admin status
    } else {
      // Handle case where token is not present (redirect to login or handle unauthorized access)
      console.error('No access token found. Redirecting to login.');
      // Example: Redirect to login page
      // history.push('/login');
    }
    fetchProducts();
  }, []);

  const checkAdminStatus = async (token) => {
    try {
      // Make an API call to verify admin status with token
      const response = await ProductService.checkAdmin(token);
      setIsAdmin(response.isAdmin); // Set isAdmin based on API response
    } catch (error) {
      console.error('Error checking admin status:', error);
      // Handle error (e.g., show error message, redirect to login)
      // Example: Redirect to login page
      // history.push('/login');
    }
  };

  const fetchProducts = async () => {
    try {
      const productsData = await ProductService.getAllProducts();
      setProducts(productsData);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const fetchProductById = async (productId) => {
    try {
      const product = await ProductService.getProductById(productId);
      setProductData({
        name: product.name,
        price: product.price,
        description: product.description,
        quantity: product.quantity,
        category_id: product.category_id,
        image: product.image
      });
      setShowUpdateForm(true); // Show update form on fetch
    } catch (error) {
      console.error(`Error fetching product ${productId}:`, error);
    }
  };

  const handleCreateProduct = async () => {
    try {
      const response = await ProductService.createProduct(productData);
      setMessage(response.message || 'Product created successfully');
      fetchProducts();
      clearProductData();
    } catch (error) {
      setMessage(`Error creating product: ${error.message}`);
    }
  };

  const handleUpdateProduct = async (productId, updatedData) => {
    try {
      const response = await ProductService.updateProduct(productId, updatedData);
      setMessage(response.message || 'Product updated successfully');
      fetchProducts();
      setShowUpdateForm(false); // Hide update form after update
    } catch (error) {
      setMessage(`Error updating product: ${error.message}`);
    }
  };

  const handleDeleteProduct = async (productId) => {
    try {
      const response = await ProductService.deleteProduct(productId);
      setMessage(response.message || 'Product deleted successfully');
      fetchProducts();
      setShowDeleteForm(false); // Hide delete form after delete
    } catch (error) {
      setMessage(`Error deleting product: ${error.message}`);
    }
  };

  const clearProductData = () => {
    setProductData({
      name: '',
      price: '',
      description: '',
      quantity: '',
      category_id: '',
      image: ''
    });
  };

  const toggleCreateForm = () => {
    setShowCreateForm(!showCreateForm);
    setShowUpdateForm(false); // Hide update form
    setShowDeleteForm(false); // Hide delete form
  };

  const toggleUpdateForm = () => {
    setShowUpdateForm(!showUpdateForm);
    setShowCreateForm(false); // Hide create form
    setShowDeleteForm(false); // Hide delete form
  };

  const toggleDeleteForm = () => {
    setShowDeleteForm(!showDeleteForm);
    setShowCreateForm(false); // Hide create form
    setShowUpdateForm(false); // Hide update form
  };

  return (
    <div className="container">
      {isAdmin ? (
        <div className="section">
          <h3>
            <a href="#" onClick={toggleCreateForm}>Create Product</a>
          </h3>
          {showCreateForm && (
            <div className="create-product-form">
              <div className="input-field">
                <label>Name:</label>
                <input type="text" value={productData.name} onChange={(e) => setProductData({ ...productData, name: e.target.value })} />
              </div>
              <div className="input-field">
                <label>Price:</label>
                <input type="text" value={productData.price} onChange={(e) => setProductData({ ...productData, price: e.target.value })} />
              </div>
              <div className="input-field">
                <label>Description:</label>
                <textarea value={productData.description} onChange={(e) => setProductData({ ...productData, description: e.target.value })} />
              </div>
              <div className="input-field">
                <label>Quantity:</label>
                <input type="text" value={productData.quantity} onChange={(e) => setProductData({ ...productData, quantity: e.target.value })} />
              </div>
              <div className="input-field">
                <label>Category ID:</label>
                <input type="text" value={productData.category_id} onChange={(e) => setProductData({ ...productData, category_id: e.target.value })} />
              </div>
              <div className="input-field">
                <label>Image:</label>
                <input type="text" value={productData.image} onChange={(e) => setProductData({ ...productData, image: e.target.value })} />
              </div>
              <div className="button-group">
                <button onClick={handleCreateProduct}>Create Product</button>
                <button onClick={toggleCreateForm}>Cancel</button>
              </div>
              {message && <p className="message">{message}</p>}
            </div>
          )}

          <div className="section">
            <h3>
              <a href="#" onClick={toggleUpdateForm}>Update Product</a>
            </h3>
            {showUpdateForm && (
              <div className="update-product-form">
                <div className="input-field">
                  <label>Name:</label>
                  <input type="text" value={productData.name} onChange={(e) => setProductData({ ...productData, name: e.target.value })} />
                </div>
                <div className="input-field">
                  <label>Price:</label>
                  <input type="text" value={productData.price} onChange={(e) => setProductData({ ...productData, price: e.target.value })} />
                </div>
                <div className="input-field">
                  <label>Description:</label>
                  <textarea value={productData.description} onChange={(e) => setProductData({ ...productData, description: e.target.value })} />
                </div>
                <div className="input-field">
                  <label>Quantity:</label>
                  <input type="text" value={productData.quantity} onChange={(e) => setProductData({ ...productData, quantity: e.target.value })} />
                </div>
                <div className="input-field">
                  <label>Category ID:</label>
                  <input type="text" value={productData.category_id} onChange={(e) => setProductData({ ...productData, category_id: e.target.value })} />
                </div>
                <div className="input-field">
                  <label>Image:</label>
                  <input type="text" value={productData.image} onChange={(e) => setProductData({ ...productData, image: e.target.value })} />
                </div>
                <div className="button-group">
                  <button onClick={() => handleUpdateProduct(productData.id, productData)}>Update Product</button>
                  <button onClick={toggleUpdateForm}>Cancel</button>
                </div>
                {message && <p className="message">{message}</p>}
              </div>
            )}
          </div>

          <div className="section">
            <h3>
              <a href="#" onClick={toggleDeleteForm}>Delete Product</a>
            </h3>
            {showDeleteForm && (
              <div className="delete-product-form">
                <div className="input-field">
                  <label>Product ID:</label>
                  <input type="text" value={productData.id} onChange={(e) => setProductData({ ...productData, id: e.target.value })} />
                </div>
                <div className="button-group">
                  <button onClick={() => handleDeleteProduct(productData.id)}>Delete Product</button>
                  <button onClick={toggleDeleteForm}>Cancel</button>
                </div>
                {message && <p className="message">{message}</p>}
              </div>
            )}
          </div>

          <div className="section">
            <ul>
              {products.map((product) => (
                <li key={product.id}>
                  {product.name} - ${product.price}
                  <button onClick={() => fetchProductById(product.id)}>Edit</button>
                  <button onClick={() => handleDeleteProduct(product.id)}>Delete</button>
                </li>
              ))}
            </ul>
          </div>
        </div>
      ) : (
        <p>You are not authorized to view this content.</p>
      )}
    </div>
  );
}

export default Dashboard;
