import axios from 'axios';

const BASE_URL = 'http://localhost:5000/api/v1/products'; // Adjust base URL as needed

const ProductService = {
  createProduct: async (productData) => {
    try {
      const response = await axios.post(`${BASE_URL}/create`, productData);
      return response.data;
    } catch (error) {
      console.error('Error creating product:', error);
      throw error; // Optionally handle or rethrow the error
    }
  },

  getProduct: async (productId) => {
    try {
      const response = await axios.get(`${BASE_URL}/update/${productId}`);
      return response.data;
    } catch (error) {
      console.error(`Error fetching product ${productId}:`, error);
      throw error; // Optionally handle or rethrow the error
    }
  },

  updateProduct: async (productId, productData) => {
    try {
      const response = await axios.put(`${BASE_URL}/${productId}`, productData);
      return response.data;
    } catch (error) {
      console.error(`Error updating product ${productId}:`, error);
      throw error; // Optionally handle or rethrow the error
    }
  },

  deleteProduct: async (productId) => {
    try {
      const response = await axios.delete(`${BASE_URL}/${productId}`);
      return response.data;
    } catch (error) {
      console.error(`Error deleting product ${productId}:`, error);
      throw error; // Optionally handle or rethrow the error
    }
  },
};

export default ProductService;
