import React, { useState } from 'react';
import './order.css';
import backgroundImage from '../images/ordert.jpg'; // Adjust the path based on your project structure

const OrderNow = () => {
  const [formData, setFormData] = useState({
    quantity: '',
    price: '',
    special_instructions: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://127.0.0.1:5000/api/v1/orders/create', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const responseData = await response.json();

      if (!response.ok) {
        throw new Error(responseData.error || 'Order creation failed');
      }

      console.log(responseData.message);
      // Optionally handle success, e.g., show a success message, redirect, etc.

      // Reset form fields after successful submission
      setFormData({
        quantity: '',
        price: '',
        special_instructions: ''
      });

    } catch (error) {
      console.error('Error creating order:', error.message);
      // Optionally handle error, e.g., show an error message to the user
      alert('Failed to create order: ' + error.message);
    }
  };

  return (
    <div className="container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <h2>Order Now</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="quantity">Quantity:</label>
          <input
            type="number"
            id="quantity"
            name="quantity"
            value={formData.quantity}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="price">Price:</label>
          <input
            type="number"
            id="price"
            name="price"
            value={formData.price}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="special_instructions">Special Instructions:</label>
          <textarea
            id="special_instructions"
            name="special_instructions"
            value={formData.special_instructions}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default OrderNow;
