import React, { useState, useEffect } from 'react';
import './order.css';
import backgroundImage from '../images/ordert.jpg'; // Adjust the path based on your project structure
import axios from 'axios';

const API_URL_PRODUCTS = 'http://127.0.0.1:5000/api/v1/products';
const API_URL_ORDERS = 'http://127.0.0.1:5000/api/v1/orders/create';

const OrderNow = () => {
  const [formData, setFormData] = useState({
    product_id: '',
    quantity: '',
    price: '',
    special_instructions: ''
  });

  const [products, setProducts] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get(API_URL_PRODUCTS);
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
        setError('Failed to load products.');
      }
    };

    fetchProducts();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(API_URL_ORDERS, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const responseData = await response.json();

      if (!response.ok) {
        throw new Error(responseData.error || 'Order creation failed');
      }

      console.log(responseData.message);
      // Optionally handle success, e.g., show a success message, redirect, etc.

      // Reset form fields after successful submission
      setFormData({
        product_id: '',
        quantity: '',
        price: '',
        special_instructions: ''
      });

    } catch (error) {
      console.error('Error creating order:', error.message);
      // Optionally handle error, e.g., show an error message to the user
      alert('Failed to create order: ' + error.message);
    }
  };

  return (
    <div className="container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <h2>Order Now</h2>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="product">Select:</label>
          <select
            id="product"
            name="product_id"
            value={formData.product_id}
            onChange={handleChange}
            required
          >
            <option value="" disabled>Select a product</option>
            {products.map(product => (
              <option key={product.id} value={product.id}>
                {product.name}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor="quantity">Quantity:</label>
          <input
            type="number"
            id="quantity"
            name="quantity"
            value={formData.quantity}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="price">Price:</label>
          <input
            type="number"
            id="price"
            name="price"
            value={formData.price}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="special_instructions">Special Instructions:</label>
          <textarea
            id="special_instructions"
            name="special_instructions"
            value={formData.special_instructions}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default OrderNow;
