import React from 'react';
import { Link, Routes, Route, useParams } from 'react-router-dom';
import weddingCakeImage from '../images/wed9.png';
import blueberryMuffinImage from '../images/muff6.png';
import glazedDoughnutImage from '../images/doug2.png';
import ShopInStore from './shopInStore'; // Import ShopInStore component

const products = [
  { id: 1, name: "Fondant Wedding Cake", description: "A sophisticated wedding cake covered in smooth fondant icing.", image: weddingCakeImage, category: "cakes" },
  { id: 2, name: "Blueberry Muffin", description: "Delicious blueberry muffin", image: blueberryMuffinImage, category: "muffins" },
  { id: 3, name: "Glazed Doughnut", description: "Classic glazed doughnut", image: glazedDoughnutImage, category: "doughnuts" }
];

const OrderOnline = () => {
  return (
    <div className="order-online-container">
      <header className="header">
        <div className="header-content">
          <h1>Welcome to Faith's Store</h1>
          <p>Shop now for the latest trends!</p>
        </div>
      </header>
      <main className="main-content">
        <section className="category-list">
          <h2>Categories</h2>
          <ul>
            <li><Link to="/order-online/productcakes">Cakes</Link></li>
            <li><Link to="/order-online/muffins">Muffins</Link></li>
            <li><Link to="/order-online/doughnuts">Doughnuts</Link></li>
          </ul>
        </section>
        <section className="product-list">
          {/* Nested Routes for ShopInStore and FilteredProducts */}
          <Routes>
            <Route path="/order-online/shop-in-store" element={<ShopInStore />} />
            <Route path="/order-online/:category" element={<FilteredProducts />} />
          </Routes>
        </section>
        <section className="sidebar">
          <div className="sidebar-content">
            {/* Additional sidebar content can be added here */}
          </div>
        </section>
      </main>
    </div>
  );
};

// Component to render filtered products based on category
const FilteredProducts = () => {
  const { category } = useParams(); // Get category from URL params

  // Filter products based on category
  const filteredProducts = products.filter(product => product.category === category);

  return (
    <section className="product-list">
      {filteredProducts.length > 0 ? (
        filteredProducts.map(product => (
          <div key={product.id} className="product-item">
            <img src={product.image} alt="Product" />
            <h2>{product.name}</h2>
            <p>{product.description}</p>
            <button className="btn-buy">Buy Now</button>
          </div>
        ))
      ) : (
        <div className="product-item">
          <p>No products found in this category.</p>
        </div>
      )}
    </section>
  );
};

export default OrderOnline;
