import React, { useState } from 'react';
import './order.css';
import backgroundImage from '../images/orderrr.jpg'; // Adjust the path based on your project structure

const API_URL_ORDERS = 'http://127.0.0.1:5000/api/v1/orders/create';

// Define price options
const priceOptions = [
  { label: '1/2 Kg - 70,000', value: '0.5:70000' },
  { label: '1 Kg - 50,000', value: '1:50000' },
  { label: '2 Kg - 80,000', value: '2:80000' },
  { label: '3 Kg - 100,000', value: '3:100000' },
  { label: '4 Kg - 140,000', value: '4:140000' },
  { label: '5 Kg - 200,000', value: '5:200000' },
];

const OrderNow = () => {
  const [formData, setFormData] = useState({
    product_id: '',
    quantity: '1', // Default quantity
    price: 50000, // Default price for 1 Kg
    special_instructions: ''
  });

  const [error, setError] = useState('');

  // Update form data and price based on selected option
  const handlePriceChange = (e) => {
    const [quantity, price] = e.target.value.split(':');

    setFormData({
      ...formData,
      quantity,
      price: parseInt(price, 10)
    });
  };

  // Update quantity in form data
  const handleQuantityChange = (e) => {
    const quantity = e.target.value;
    const priceOption = priceOptions.find(option => option.value.startsWith(quantity));
    const price = priceOption ? parseInt(priceOption.value.split(':')[1], 10) : formData.price;

    setFormData({
      ...formData,
      quantity,
      price
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(API_URL_ORDERS, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const responseData = await response.json();

      if (!response.ok) {
        throw new Error(responseData.error || 'Order creation failed');
      }

      console.log(responseData.message);
      // Optionally handle success, e.g., show a success message, redirect, etc.

      // Reset form fields after successful submission
      setFormData({
        product_id: '',
        quantity: '1',
        price: 50000,
        special_instructions: ''
      });

    } catch (error) {
      console.error('Error creating order:', error.message);
      // Optionally handle error, e.g., show an error message to the user
      alert('Failed to create order: ' + error.message);
    }
  };

  return (
    <div className="container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <h2>Order Now</h2>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Select Product:</label>
          <select
            name="product_id"
            value={formData.product_id}
            onChange={(e) => setFormData({ ...formData, product_id: e.target.value })}
            required
          >
            <option value="" disabled>Select a product</option>
            <option value="wedding_cake">Wedding Cake</option>
            <option value="birthday_cake">Birthday Cake</option>
            <option value="baby_shower_cake">Baby Shower Cake</option>
            <option value="graduation_cake">Graduation Cake</option>
            <option value="anniversary_cake">Anniversary Cake</option>
            <option value="blueberry_muffin">Blueberry Muffin</option>
            <option value="chocolate_chip_muffin">Chocolate Chip Muffin</option>
            <option value="banana_nut_muffin">Banana Nut Muffin</option>
            <option value="lemon_poppy_seed_muffin">Lemon Poppy Seed Muffin</option>
            <option value="pumpkin_spice_muffin">Pumpkin Spice Muffin</option>
            <option value="glazed_doughnut">Glazed Doughnut</option>
            <option value="chocolate_frosted_doughnut">Chocolate Frosted Doughnut</option>
            <option value="jelly_filled_doughnut">Jelly Filled Doughnut</option>
            <option value="cinnamon_sugar_doughnut">Cinnamon Sugar Doughnut</option>
            <option value="maple_bacon_doughnut">Maple Bacon Doughnut</option>
          </select>
        </div>
        <div>
          <label>Price:</label>
          <select
            name="price"
            value={`${formData.quantity}:${formData.price}`}
            onChange={handlePriceChange}
            required
          >
            <option value="" disabled>Price</option>
            {priceOptions.map(option => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Quantity:</label>
          <input
            name="quantity"
            type="number"
            min="1"
            value={formData.quantity}
            onChange={handleQuantityChange}
            required
          />
        </div>
        <div>
          <label>Special Instructions:</label>
          <textarea
            name="special_instructions"
            value={formData.special_instructions}
            onChange={(e) => setFormData({ ...formData, special_instructions: e.target.value })}
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default OrderNow;
